INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Administração', '8 semestres', 'O curso de administração geralmente é encontrado em nível de bacharel, onde são formados profissionais capazes de gerenciar recursos e materiais, financeiros e humanos de uma empresa ou instituição.', '/assets/cursos/adm.webp');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Administração Pública', '8 semestres', 'Um profissional em administração púclica é capaz de gerenciar recursos materiais, econômicos e humanos relacionados a estrutura política, jurídica e histórica do poder público.', '/assets/cursos/admpub.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Agronomia', '10 semestres', 'O aluno de agronomia aprende a prática e a teoria relacionada a produção agrícola, sabendo dominar recursos naturais e o caminhar da agroindústria.', '/assets/cursos/agr.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Arquitetura e Urbanismo', '10 semestres', 'Forma um profissional capaz de projetar, desenhar, organizar e criar espaços de construção, também sabendo atuar em preservação, restauração e conservação de estruturas existentes.', '/assets/cursos/arq.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Arquivologia', '8 semestres', 'O arquivista é o profissional capacitado para gestão, presevação e restauração de documentos, como fotos, vídeos, textos históricos e registros. ', '/assets/cursos/arqu.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Artes Visuais', '8 semestres', 'Desenho, pintura, escultura, fotografia, instalação e leitura de obras são algumas das capacidades que se tende a aprender no curso de artes visuais.', '/assets/cursos/art.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Biblioteconomia', '8 semestres', 'O profissional formado em biblioteconomia tem a capacidade de cuidar de acervos, bibliotecas, museus, editoras e tem a habilidade de catalogar, divulgar, classificar e organizar.', '/assets/cursos/bib.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Biomedicina', '10 semestres', 'É a ciência de pesquisa biológica por trá da medicina onde o profissional. O biomédico é responsável por pesquisas, exames e diagnósticos laboratorias. ', '/assets/cursos/bio.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências Aeronáuticas', '8 semestres', 'É um curso bacharel onde o aluno adquiri os conhecimentos para projetar e realizar manutenções de aeronaves, bem como gerenciar tráfego aéreo.', '/assets/cursos/aer.jpeg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências da Religião', '8 semestres', 'É um curso da área de humanas onde o profissional se aprofunda nas religiões que existem pelo mundo e aprende sobre suas características e efeitos sobre a sociedade.', '/assets/cursos/cierel.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências Biológicas', '8 semestres', 'Disponível tanto em licenciatura quanto em bacharel o aluno de biologia aprende um pouco sobre cada campo como ecologia, citologia, genética, entre outros.', '/assets/cursos/bio.png');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências da Computação', '8 semestres', 'A ciência da computação é o curso que se aprofunda na área de desenvolvimento de programas para diferentes dispositivos.', '/assets/cursos/ciecom.jpeg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências Contábeis', '8 semestres', 'Quem faz o curso de Ciência Contábeis aprende a analisar e gerenciar questões econômicas e financeiras de instiutições ou pessoas.', '/assets/cursos/ciecon.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências Econômicas', '8 semestres', 'Este curso forma profissionais aptos a analisar processos de distribuição de renda e produção de bens e serviços.', '/assets/cursos/cieeco.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Ciências Sociais', '8 semestres', 'Estuda as estruturas e relações dos membros da sociedade em módulos como cultura, ensino, política, economia, entre outros.', '/assets/cursos/ciesoc.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Cinema e Audiovisual', '8 semestres', 'O curso aborda todas as características que formam o cinema como, fotografia, roteiro, cenário, direção, edição e todos os atributos necessário a uma produção.', '/assets/cursos/cinaud.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Computação', '8 semestres', 'Aprende a desenvolver sistemas para computadores e microcomputadores da ideia até a implementação. Da área de exatas se faz necessário ter conhecimentos de matemática e raciocínio lógico.', '/assets/cursos/com.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Comunicação Social', '8 semestres', 'Quem faz comunicação social aprende a realizar comunicação em massa e produzir campanhas de grande alcance que gere atos de cidadania.', '/assets/cursos/comu.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Dança', '8 semestres', 'Forma profissionais capazes de produzir, ensinar, e analisar diferentes danças no mundo assim como suas bases, histórias e técnicas.', '/assets/cursos/dan.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Desing Digital', '8 semestres', 'Forma o profissional capaz de usar o conhecimento e gostos de um grupo para criar produtos digitais, gráficos ou físicos com base em forma, estrutura, estética e utilidade.', '/assets/cursos/desmod.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Desing Gráfico', '.', '.', '.');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Direito', '10 semestres', 'Forma profissionais preparados para aplicação de leis ou normas jurídicas a fim de obter justiça e ajudar alguém ou causa.', '/assets/cursos/dir.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Economia Doméstica', '8 semestres', 'Indo um pouco além do conhecimento convencional da palavra "doméstica", no curso se aprende as bases de nutrição, economia, administração, educação e serviço social. ', '/assets/cursos/ecodom.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Educação Física', '8 semestres', 'Os profissionais de educação física aprendem o funcionamento mecânico do corpo e seus efeitos, e se tornam capazes de promover a saúde por meio de ensinos e práticas', '/assets/cursos/edufis.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Enfermagem', '10 semestres', 'Sendo a figura mais presente em hospitais os enfermeiros possuem conhecimento técnico e prático voltados para a necessidade de atenção primária', '/assets/cursos/enf.jpg');
