INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Engenharia Agrícola', '10 semestres', 'Os alunos de engenharia agronoma são preparados para atuar desde o planejamento da produção agrícola e agroindustrial até a comercialização dos produtos.', '/assets/cursos/engagr.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Engenharia da Computação', '10 semestres', 'O profissional da engenharia de computação tem conhecimento aprofundado em software (desenvolver sistemas) e hardware (construção e manutenção de máquinas).', '/assets/cursos/engcom.jfif');
