INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Estatística', '8 semestres', 'Forma especialistas em matemática com a capacidade de analisar e interpretar dados como coordenadas, levantamento estatístico e banco de dados', '/assets/cursos/est.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Farmácia', '10 semestres', 'Os alunos estudam a composição e produção de medicamentos, cosméticos e produtos de higiene, bem como as propriedades dos ativos comuns', '/assets/cursos/farm.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Filosofia', '8 semestres', 'O curso forma profissionais capazes de realizar análises críticas e rigorosas de diversas questões e que também conhece a história e os fundamentos dos pensamentos filosóficos.', '/assets/cursos/fil.png');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Física', '8 semestres', 'O aluno de física aprende sobre o máximo de propriedades fisicas dentro de 4 anos, como onda, matéria, energia, entre outros. Sem contar que é uma das graduações que aborda mais assuntos.', '/assets/cursos/fisica.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Fisioterapia', '10 semestres', 'O profissional em fisioterapia aprende sobre todo o funcionamento mecânico do corpo e é capaz de recuperar movimentos, fortalecer e promover saúde por técnicas e exercícios.', '/assets/cursos/fisi.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Fonoaudiologia', '8 semestres', 'O fonoaudiólogo estuda formas de linguagem e comunicação bem como suas técnicas sendo capaz de identificar e tratar problemas como surdez, gagueira, e dicção incorreta', '/assets/cursos/fon.png');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Geografia', '10 semestres', 'O curso de geografia ensina sobre toda a superfície terrestre, o que nela existe e todos os efeitos do homem sobre a terra, dessa forma o geógrafo é capaz de analizar todas as relações com o planeta.', '/assets/cursos/geo.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Geologia', '10 semestres', 'O aluno de geologia aprende sobre toda a composição, estrutura e evolução do planeta Terra, dessa forma ele analisa rochas, montanhar, superfície, o fundo de meios marinhos, entre outros.', '/assets/cursos/geol.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Gestão de Recursos Humanos', '.', '.', '.');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('História', '8 semestres', 'O bacharelado em história ensina sobre o caminhar da humanidade desde o mais antigo dos registros até os dias atuais variando entre áreas como cultura, religião, economia e estilo de vida.', '/assets/cursos/his.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Jornalismo', '8 semestres', 'O curso de jornalismo ensina os alunos a formularem notícias para todos os meios de comunicação existentes, ensiando a pontuar fatos, passar clareza, averiguar informações e investigar casos.', '/assets/cursos/jor.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Letras', '8 semestres', 'O aluno de letras aprende sobre a história de uma lingua, seus dialetos, evoluções, dicção e gramática. E além disso aprende a avaliar obras literárias.', '/assets/cursos/let.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Matemática', '8 semestres', 'A faculdade de Matemática forma profissionais que conseguem usar a lógica para elaborar teorias, testar hipóteses, desenvolver aplicações de cálculos na pesquisa e nas ciências.', '/assets/cursos/mat.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Medicina', '12 semestres', 'Os médicos aprendem sobre todo o funcionamento do corpo e suas possíveis doenças sendo capazes de identificar e tratar problemas que aflingem aos seres humanos.', '/assets/cursos/med.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Medicina Veterinária', '8 semestres', 'Os médicos veterinários aprendem sobre o funcionamento do corpo de várias espécies geralmente separados em pequeno e grande porte, dessa forma sabem tratar possíveis doenças.', '/assets/cursos/vet.webp');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Meteorologia', '8 semestres', 'Os profissionais do ramo da metereologia atuam estudando as interações entre os estados físicos e químicos da atmosfera do nosso planeta.', '/assets/cursos/met.jfif');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Museologia', '8 semestres', 'O museólogo dedica-se à classificação, à conservação e à exposição de peças de valor histórico, artístico, cultural e científico.', '/assets/cursos/mus.webp');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Musica', '8 semestres', 'O curso de Música forma profissionais para o mercado de trabalho da arte musical, bem como para atuar como professores.', '/assets/cursos/musi.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Nutrição', '8 semestres', 'A faculdade de Nutrição prepara o aluno para fazer diagnósticos nutricionais e elaborar dietas adequadas às necessidades de diferentes pessoas.', '/assets/cursos/nut.webp');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Oceanologia', '10 semestres', 'O trabalho de um oceanógrafo envolve coletar amostras dos ambientes aquáticos, determinar as condições físicas, químicas e biológicas destes ambientes.', '/assets/cursos/oce.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Odontologia', '10 semestres', 'O profissional da Odontologia se dedica ao cuidado e ao tratamento dos dentes, da boca e dos ossos da face.', '/assets/cursos/odo.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Pedagogia', '8 semestres', 'O curso de Pedagogia forma um profissional multidisciplinar, capaz de atuar nos processos de ensino e aprendizagem, tanto para alfabetizar crianças, jovens e adultos.', '/assets/cursos/ped.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Psicologia', '12 semestres', 'A Psicologia estuda os fenômenos psíquicos e comportamentais do ser humano por meio de uma análise complexa e criteriosa das emoções dos indivíduos.', '/assets/cursos/psi.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Publicidade', '8 semestres', 'O publicitário é um profissional que cria campanhas e peças publicitárias. Ou seja, ele trabalha com propagandas, apresentando um produto ou serviço ao consumidor.', '/assets/cursos/pub.jpg');
