INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Relações Públicas', '8 semestres', 'O objetivo do curso é é habilitar o aluno a atuar em organizações públicas, privadas e do terceiro setor, tendo todo conhecimento para cuidar da comunicação e do relacionamento.', '/assets/cursos/rp.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Saúde coletiva', '8 semestres', 'Este profissional é considerado como um trabalhador do campo da Saúde Coletiva, com formação generalista, que atua em todos os níveis de complexidade do SUS.', '/assets/cursos/sap.jpeg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Secretariado', '3 semestres', 'Forma profissionais preparados para o assessoramento, podendo atuar tanto diretamente com o empresário, organizando seus compromissos, quanto com a automação de escritórios.' , '/assets/cursos/sec.jpeg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Serviço Social', '8 semestres', 'O profissional trabalha para garantir à sociedade o acesso à educação, saúde, alimentação, moradia, emprego e outros direitos fundamentais do cidadão.', '/assets/cursos/serv.jpg');
INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Sistemas de Informação', '8 semestres', 'Gerenciar dados que são gerados em ambientes tecnológicos internos e externos, organizando-os em softwares, sites ou bancos de dados.', '/assets/cursos/si.webp');
