INSERT INTO cursos(nome_curso, duracao, descricao, img) VALUES ('Terapia Ocupacional', '8 semestres', 'O terapeuta ocupacional estuda e emprega atividades de trabalho e lazer no tratamento de distúrbios físicos ou mentais e de desajustes emocionais e sociais.', '/assets/cursos/teoc.jpg');
